import axios from "axios";
import React, { useEffect, useState } from "react";
import { Card } from "react-bootstrap";
import Detail from "../view/routing/Detail";
import logo from "../assets/image/errImg.png";

function Product() {
  const [productData, setProductData] = useState([]);
  const [product, setProduct] = useState({});
  const [toggle, setToggle] = useState(false);

  useEffect(() => {
    getProduct();
  }, []);

  const handleOpen = (data) => {
    setToggle(true);
    setProduct({ ...data });
  };

  const handleClose = () => {
    setToggle(false);
  };

  const getProduct = () => {
    axios
      .get("product")
      .then((result) => {
        // console.log(result.data.data, "prduct........");
        setProductData(result.data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // console.log(product);
  return (
    <>
      {productData.map((item, i) => (
        <div onClick={() => handleOpen(item)} className="col-2 " key={i}>
          <div
            style={{
              width: "auto",
              height: 250,
              marginBottom: 16,
            }}
          >
            <Card.Img variant="top" src={logo} style={{ border: "0.8px solid #E0E0E5" }} />

            <div style={{ color: "#ADADB9", fontSize: 12 }}>
              <p style={{ color: "#33334F", fontSize: 16 }}>{item.title}</p>
              <p>{item.created_by.name}</p>
              <p>{item.created_at.slice(0, 10)}</p>
            </div>
          </div>
        </div>
      ))}
      <Detail toggle={toggle} close={handleClose} data={product} />
    </>
    // </div>
  );
}

export default Product;
