import * as React from "react";
import Box from "@mui/material/Box";
import { Modal, Button } from "@mui/material";
import { Card } from "react-bootstrap";
import logo from "../../assets/image/errImg.png";
const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};

export default function Detail({ toggle, close, data }) {
  return (
    <Modal
      open={toggle}
      onClose={close}
      aria-labelledby="modal-modal-title"
      aria-describedby="modal-modal-description"
    >
      <Box sx={style}>
        {data.title}
        <Card>
          <Card.Img variant="top" src={logo} style={{ border: "0.8px solid #E0E0E5" }} />
        </Card>
        <p>Product Detail</p>
        <p>Price: {data.price}</p>
        <p>Offer_Price: {data.offer_price}</p>
        <Button style={{ background: "#917be7 ", color: "white" }}>ADD TO CART</Button>
      </Box>
    </Modal>
  );
}
