import React, { useState } from "react";
import { TextField, Button } from "@mui/material";
import "../../css/addproduct.css";
function Addproduct() {
  const [file, setFile] = useState([]);

  const fileSelectHandler = (e) => {
    if (e.target.files) {
      const filesArray = Array.from(e.target.files).map((file) => URL.createObjectURL(file));
      console.log(filesArray);
    }
    console.log(e.target.files);
  };
  return (
    <div>
      <form>
        <div style={{ width: 350 }}>
          <h3 className="h-line">Add Product</h3>
          <label>Choose Category</label>

          <TextField
            fullWidth
            id="outlined-select-currency"
            select
            label="Choose Category"
            value={""}
          />

          <label>Product Title</label>

          <TextField fullWidth id="outlined-textarea" label="Add Product title" />

          <label>Discripition</label>

          <TextField
            fullWidth
            id="outlined-textarea"
            label="Write Something about Product"
            multiline
            rows={4}
          />

          <label>Product Photos</label>

          <input type="file" multiple id="file" onChange={fileSelectHandler} />

          <p className="p-tag">Review your Details</p>
          <label>Email Address</label>

          <TextField fullWidth id="demo-helper-text-aligned-no-helper" label="Add Email Address" />

          <label>Add Address</label>

          <TextField fullWidth id="demo-helper-text-aligned-no-helper" label="Add Address" />

          <Button variant="contained" style={{ marginTop: 10, marginLeft: 233 }}>
            Contained
          </Button>
        </div>
      </form>
    </div>
  );
}

export default Addproduct;
