import axios from "axios";
import React, { useEffect, useState } from "react";
import { Card } from "react-bootstrap";
// import { IMAGE_URL } from "../helper/helper";
import logo from "../assets/image/errImg.png";

function Product() {
  const [productData, setProductData] = useState([]);
  const [imgDetail, setImgDetail] = useState(false);

  useEffect(() => {
    getProduct();
  }, []);

  const handleShowDetail = () => {
    setImgDetail({ imgDetail: !imgDetail });
  };

  const getProduct = () => {
    axios
      .get("product")
      .then((result) => {
        console.log(result.data.data, "prduct........");
        setProductData(result.data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <>
      {productData.map((item, i) => (
        <div className="col-2 " key={i}>
          <div
            style={{
              width: "auto",
              height: 250,
              marginBottom: 16,
            }}
          >
            <Card.Img variant="top" src={logo} style={{ border: "0.8px solid #E0E0E5" }} />

            <div style={{ color: "#ADADB9", fontSize: 12 }}>
              <p style={{ color: "#33334F", fontSize: 16 }}>{item.title}</p>
              <p>{item.created_by.name}</p>
              <p>{item.created_at}</p>
            </div>
            {imgDetail && (
              <dialog
                className="dialog"
                style={{ position: "absolute" }}
                open
                onClick={this.handleShowDialog}
              >
                <Card.Img variant="top" src={logo} style={{ border: "0.8px solid #E0E0E5" }} />

                <div style={{ color: "#ADADB9", fontSize: 12 }}>
                  <p style={{ color: "#33334F", fontSize: 16 }}>{item.title}</p>
                  <p>{item.created_by.name}</p>
                  <p>{item.created_at}</p>
                </div>
              </dialog>
            )}
          </div>
        </div>
      ))}
    </>
    // </div>
  );
}

export default Product;
