import React, { useEffect, useState } from "react";
import "../../css/header.css";
import SearchIcon from "@mui/icons-material/Search";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import { ToastContainer, toast } from "react-toastify";
import Button from "@mui/material/Button";
import LocationOnIcon from "@mui/icons-material/LocationOn";
import { Navbar, NavDropdown, Container, Card } from "react-bootstrap";
import logo from "../../assets/logo/Logo.png";
import Clothe from "../../assets/logo/clothes.png";
import Book from "../../assets/logo/books.png";
import Electronic from "../../assets/logo/electronics.png";
import Health from "../../assets/logo/health.png";
import Sneakers from "../../assets/logo/sneakers.png";
import Furniture from "../../assets/logo/furnitures.png";
import { useHistory } from "react-router-dom";
import axios from "axios";
function Header() {
  const [categoryData, setCategoryData] = useState([]);
  useEffect(() => {
    getCategory();
  }, []);
  console.log(categoryData);
  const getCategory = () => {
    axios
      .get("categories")
      .then((result) => {
        setCategoryData(result.data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // const getProduct = () => {
  //   axios
  //     .get("product")
  //     .then((res) => {
  //       console.log(res);
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  // };
  const history = useHistory();

  const logOut = () => {
    localStorage.clear();
    history.push("/login");
  };

  return (
    <div>
      <ToastContainer limit={1} />
      <Navbar bg="light" expand="lg">
        <Container fluid className="d-flex justify-content-between px-5">
          <div className="d-flex justify-content-around w-50">
            <img src={logo} alt="" />
            <div className="d-flex">
              <input type="text" placeholder="search for product" className="searchTerm" />
              <button type="submit" className="searchButton">
                <SearchIcon />
              </button>
            </div>
          </div>

          <div className="btn-div">
            <Button variant="contained" className="mx-5 btn">
              Donate Item
            </Button>
            <div className="btn-line  "></div>
            <div className="location">
              <LocationOnIcon />
            </div>
            <div className="dropdown">
              <NavDropdown title="State" id="basic-nav-dropdown" style={{ color: "black" }}>
                <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
              </NavDropdown>
            </div>
            <div className="account d-flex">
              <AccountCircleIcon />
            </div>
            <div className="acc-ddn">
              <NavDropdown title="" id="basic-nav-dropdown" style={{ color: "black" }}>
                <NavDropdown.Item href="">
                  <button onClick={logOut}>Log out</button>
                </NavDropdown.Item>
              </NavDropdown>
            </div>
          </div>
        </Container>
      </Navbar>
      <div className="heading">
        <p>Product by Categories</p>
      </div>
      <div className="ctgry-btn">
        <img src={Clothe} alt="" />
        {categoryData.map((item, i) => (
          <button key={i} className="btn1">
            {item.title}
          </button>
        ))}
      </div>
      <div className="prdt-lst">
        <p>All Products</p>
        <Card></Card>
      </div>
    </div>
  );
}

export default Header;
