import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { BrowserRouter, Routes, Navigate, Route } from "react-router-dom";
import Login from "./view/auth/Login";
import Header from "./view/home/Header";
import Signup from "./view/auth/Signup";
import axios from "axios";
import PrivateRoute from "./view/routing/PrivateRoute";

axios.defaults.baseURL = "https://groceryshop-api.herokuapp.com/api/";
axios.defaults.headers.post["Content-Type"] = "application/json";
function App() {
  const token = localStorage.getItem("user.token");

  return (
    <BrowserRouter>
      <Routes>
        <Route exact path="/" element={!token ? <Login /> : <Header />} />
        <Route exact path="/home" element={!token ? <Login /> : <Header />} />
        <Route path="/signup" element={!token ? <Signup /> : <Header />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
